$(document).ready(function(){$.getJSON("data/data.json",function(a){$(".products").prepend("<h2>"+a.response.products[0].name.toUpperCase()+"</h2>");for(var b=0;b<a.response.products[0].options.length;b++){var c=a.response.products[0].options[b];$(".products").append("<a href='#quantity'><img data-id='"+c.color+"' class='product' src='"+c.image+"'></a>"),$(".product").click(function(){$(".cart")[0].innerHTML="<h3>"+$(this).attr("data-id").toUpperCase()+"</h3><img src='"+this.src+"'>"})}})});